  grid-row: 1 / 4;
  display: grid;
  grid-template-rows: subgrid;
}

section {
  border-block: 8px solid blue;
  border-inline: 8px solid green;
}

.foo {
  --color: #4488dd;
  background-color: hsl(from var(--color) h s calc(l * 1.2))
}

.scroller {
  height: 300px;
  overflow-y: scroll;
  scroll-snap-type: y mandatory;
}

.scroller section {
  scroll-snap-align: start;
}

::view-transition-old(main-image),
::view-transition-new(main-image) {
  transform-origin: right top;
}

::view-transition-new(main-image) {
  animation: 400ms ease-out both grow;
}

figure {
  view-transition-name: main-image;
}

